mapper.select:
  	SELECT COUNT(ID) FROM MISSION M
	  	WHERE 1=1 


	  			ID = #{missionCode}



			  		AND M.TYPE = #{type}


			  		AND M.CREATETIME >= #{startTime}


			  		AND M.CREATETIME <=  #{endTime}


			  		AND M.TYPE = #{type}


			  		AND M.STATUS = #{status}


			  		AND M.SPONSOR_ID = #{sponsorId}


			  		AND M.HANDLER_ID = #{handlerId} OR
			  		 EXISTS (SELECT 1 FROM MISSION_HANDLE MH WHERE M.ID = MH.MISSIONID AND MH.HANDLER_ID = #{handlerId})



	  	LIMIT #{start},#{end}

SELECT M.ID , M.TITLE, M.CONTENT, M.CREATETIME, M.HANDLETIME, M.COMMITTIME, M.TYPE, M.STATUS, M.NOTES, SPONSOR_ID AS sponsorId, SPONSOR_NAME AS sponsorName, SPONSOR_DEP AS sponsorDep, HANDLER_ID AS handlerId, HANDLER_NAME AS handlerName, HANDLER_DEP AS handlerDep FROM MISSION M WHERE 1=1
ID = #{missionCode}
AND M.TYPE = #{type}
AND M.CREATETIME >= #{startTime}
AND M.CREATETIME <= #{endTime}
AND M.TYPE = #{type}
AND M.STATUS = #{status}
AND M.SPONSOR_ID = #{sponsorId}
AND M.HANDLER_ID = #{handlerId} OR EXISTS (SELECT 1 FROM MISSION_HANDLE MH WHERE M.ID = MH.MISSIONID AND MH.HANDLER_ID = #{handlerId})

mapper.update:
UPDATE MISSION SET

		COMMITTIME = #{commitTime},


		HANDLETIME = #{handleTime},

  STATUS = #{status}
WHERE CODE = #{code}
