mapper.select:
SELECT COUNT(ID) FROM ACCOUNT 
WHERE 1=1 AND ID != #{currId}

	AND NAME LIKE #{name}


	AND AUTH_LEVEL = #{authLevel}


	AND DEPARTMENT_CODE = #{depCode}

SELECT AC.ID, AC.NAME, AC.USERNAME, AC.PASSWORD, AC.PHONE, AC.WEIBO, AU.LEVEL AS a_level, AU.NAME AS a_name, DE.CODE AS d_code, DE.NAME AS d_name FROM ACCOUNT AC LEFT JOIN AUTH AU ON AC.AUTH_LEVEL=AU.LEVEL LEFT JOIN DEPARTMENT DE ON DE.CODE=AC.DEPARTMENT_CODE WHERE AC.ID != #{currId}
AND AC.NAME LIKE #{name}
AND AC.AUTH_LEVEL = #{authLevel}
AND AC.DEPARTMENT_CODE = #{depCode}
LIMIT #{start},#{end}

mapper.update:
UPDATE ACCOUNT SET

	NAME = #{name},


	PHONE = #{phone},

WEIBO = #{weibo},
DEPARTMENT_CODE = #{depCode}
WHERE ID = #{currId}
