mapper.select:
SELECT COUNT(ID) FROM ACCOUNT 
WHERE 1=1 AND ID > 0

	AND NAME LIKE #{name}


	AND POSTION = #{posCode}


	AND DEPARTMENT_CODE = #{depCode}

SELECT AC.ID, AC.NAME, AC.USERNAME, AC.PASSWORD, AC.PHONE, AC.WEIBO, PO.CODE AS p_code, PO.NAME AS p_name, DE.CODE AS d_code, DE.NAME AS d_name FROM ACCOUNT AC LEFT JOIN POSITION PO ON AC.POSTION = PO.CODE LEFT JOIN DEPARTMENT DE ON DE.CODE=AC.DEPARTMENT_CODE WHERE 1=1
AND AC.NAME LIKE #{name}
AND POSTION = #{posCode}
AND AC.DEPARTMENT_CODE = #{depCode}
LIMIT #{start},#{end}
