mapper.select:
SELECT COUNT(ID) FROM ACCOUNT 
WHERE 1=1

	AND NAME LIKE #{name}


	AND AUTH_LEVEL = #{level}


	AND DEPARTMENT_CODE = #{depCode}

SELECT AC.ID, AC.NAME, AC.USERNAME, AC.PASSWORD, AC.PHONE, AC.WEIBO, AU.LEVEL AS a_level, AU.NAME AS a_name, DE.CODE AS d_code, DE.NAME AS d_name FROM ACCOUNT AC, AUTH AU , DEPARTMENT DE WHERE AC.AUTH_LEVEL=AU.LEVEL AND DE.CODE=AC.DEPARTMENT_CODE
AND AC.NAME LIKE #{name}
AND AC.AUTH_LEVEL = #{level}
AND AC.DEPARTMENT_CODE = #{depCode}
LIMIT #{start},#{end}
